import React, {userState} from "react";
import './TestComp.css'
import BillForm from './NewBill/BillForm.js';
import FirstComponent from "./FirstComponent";
import Card from './Card'


const dummy = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];
const TestComp = () => {
    const[year, SetYear] = useState('2019')
    const[expense, setExpenses] = useState(dummy);
    const finalprops = (random) => {
    setExpenses((abcde) => {return [...abcde, random];})
  }
  const YearHandler = (yearprops) => {
    // console.log(yearprops)
    SetYear(yearprops);
  }
    return (
        <div>
           <BillForm newbillform={finalprops}/>
            <Card className="expenses">
                <FilterComponent selected={year} onYearchange={YearHandler}/>
      {dummy.map(expense => <FirstComponent title= {expense.title} amount={expense.amount} date={expense.date} />)}
            </Card>
        </div>
    )
}

export default TestComp;
