    const eventHandler = () => {
      SetTitle("Done!!!");
    }
    return (<Card className='expense-item'>
            <DateComponent date={props.date}/>
            <div className='expense-item__description'>
            <h2>{title}</h2>
            <div className='expense-item__price'>{props.amount}</div>
            <button onClick={eventHandler}>Title Change</button>
