import React, {useState} from 'react';
import FirstComponent from "./CustomComponents/FirstComponent";
import './App.css'
import Card from './CustomComponents/Card'
import BillForm from './CustomComponents/NewBill/BillForm.js';
import FilterComponent from './CustomComponents/FilterComponent.js'

const App = () => {
  const[year, SetYear] = useState('2019')
  const dummy = [
    {
      id: 'e1',
      title: 'Toilet Paper',
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: 'e3',
      title: 'Car Insurance',
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: 'e4',
      title: 'New Desk (Wooden)',
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  const[expense, setExpenses] = useState(dummy);
  const finalprops = (random) => {
    // console.log(props);
    setExpenses((random) => {
      return [...expense, random];
    });
  }
  const YearHandler = (yearprops) => {
    // console.log(yearprops)
    SetYear(yearprops);
  }
  return (<div>

    <BillForm newbillform={finalprops}/>
    <Card className="expenses">
      <FilterComponent selected={year} onYearchange={YearHandler}/>
      {dummy.map(expense => <FirstComponent title= {expense.title} amount={expense.amount} date={expense.date} />)}
    </Card>
    </div>
  );
}

export default App;
